public class Main implements Runnable {

    public static void main(String[] args) {
        new Thread(null, new Main(), "whatever", 1 << 26).start();
    }
//

    private FastScanner sc;
    private PrintWriter pw;

    public void run() {
        try {
            boolean isSumitting = true;
            // isSumitting = false;
            if (isSumitting) {
                pw = new PrintWriter(System.out);
                sc = new FastScanner(new BufferedReader(new InputStreamReader(System.in)));
            } else {
                pw = new PrintWriter(new BufferedWriter(new FileWriter("output.txt")));
                sc = new FastScanner(new BufferedReader(new FileReader("input.txt")));
            }
        } catch (Exception e) {
            throw new RuntimeException();
        }

        int t = sc.nextInt();
        // int t = 1;
        while (t-- > 0) {
            // sc.nextLine();
            // System.out.println("for t=" + t);
            solve();
        }
        pw.close();
    }

    // public long mod = 1_000_000_007;
    public long mod = 998_244_353;
    private class Pair {
        int first, second;
        Pair(int first, int second) {
            this.first = first - 1;
            this.second = second - 1;
        }
    }

    public void solve() {
        int n = sc.nextInt();
        int m = sc.nextInt();
        int k = sc.nextInt();
        int q = sc.nextInt();
        if (q == 0) {
            pw.println(0);
            return;
        }
        HashSet<Integer> rows = new HashSet<Integer>();
        HashSet<Integer> columns = new HashSet<Integer>();
        for (int i = 0; i < n; i++) rows.add(i);
        for (int i = 0; i < m; i++) columns.add(i);
        Stack<Pair> queries = new Stack<Pair>();
        for (int i = 0; i < q; i++) {
            queries.add(new Pair(sc.nextInt(), sc.nextInt()));
        }
        int x, y;
        int ans = 0;
        boolean found = false;
        while (queries.size() != 0) {
            x = queries.peek().first;
            y = queries.pop().second;
            found = false;
            if (rows.contains(x) && columns.size() > 0) {
                rows.remove(x);
                found = true;
            }
            if (columns.contains(y) && rows.size() > 0) {
                columns.remove(y);
                found = true;
            }
            if (found) ans++;
        }
        pw.println(fastPow((long)k, (long)ans, mod));
    }



