                    p.setRegisterDate(new java.util.Date());
                    java.lang.String captchacode = com.job.lr.web.api.PhoneRestController.getRandomString(Constants.CaptchacodeSize);
                    p.setCaptchacode(captchacode);
                    com.job.sendSms.SDKSendTemplateSMS s = new com.job.sendSms.SDKSendTemplateSMS();
                    java.lang.Integer SMS_Gap_TimeI = com.job.lr.filter.Constants.SMS_Gap_Time;
                    java.lang.String message = s.SendTemplateSMS(p.getPhonenumber(), captchacode, "1", SMS_Gap_TimeI.toString());
                    java.lang.String sendOkflag = "sendok";
                    if (sendOkflag.equals(message)) {
                        accountService.updatePhonenumber(p);
                        returnCode = phonestatus_not_activated;
                    }else {
                        returnCode = phone_no_send_sms;
                    }
                }else {
                    phoneRegisterDate = p.getRegisterDate();
                    returnCode = phonestatus_not_activated_flag;
                }
            }else
                if (nowstatus == phonestatus_activated) {
                    returnCode = phonestatus_activated_flag;
                }

        }
    }
    com.job.lr.entity.GeneralResponse gp = new com.job.lr.entity.GeneralResponse();
    gp.setRetCode(returnCode);
    if (returnCode == phonestatus_not_activated_flag) {
        java.text.SimpleDateFormat myFmt = new java.text.SimpleDateFormat("yyyyMMddHHmmss");
        java.lang.String phoneRegisterDateString = myFmt.format(phoneRegisterDate);
        gp.setRetInfo(((("," + (com.job.lr.filter.Constants.SMS_Gap_Time)) + ".:") + phoneRegisterDateString));
    }else
        if (returnCode == phonestatus_activated_flag) {
            gp.setRetInfo("");
        }else
            if (returnCode == phone_sms_not_send_flag) {
                gp.setRetInfo("");
            }else
                if (returnCode == phone_null_flag) {
                    gp.setRetInfo("");
                }else {
                    gp.setRetInfo("phone1002");
                }



    return gp;
}

