import java.io.*;
import java.util.*;

public class Task {

    public static void main(String[] args) throws Exception {

        new Task().go();
    }

    PrintWriter out;
    Reader in;
    BufferedReader br;

    Task() throws IOException {

        try {

            //br = new BufferedReader( new FileReader("input.txt") );
            //in = new Reader("input.txt");
            in = new Reader("input.txt");
            out = new PrintWriter( new BufferedWriter(new FileWriter("output.txt")) );
        }
        catch (Exception e) {

            //br = new BufferedReader( new InputStreamReader( System.in ) );
            in = new Reader();
            out = new PrintWriter( new BufferedWriter(new OutputStreamWriter(System.out)) );
        }
    }

    void go() throws Exception {

        //int t = in.nextInt();
        int t = 1;
        while (t > 0) {
            solve();
            t--;
        }

        out.flush();
        out.close();
    }


    int inf = 2000000000;
    int mod = 1000000007;
    double eps = 0.000000001;

    int n;
    int m;

    ArrayList<Integer>[] g;
    int[][] buity = new int[5000][5000];
    void solve() throws IOException {
        int n = in.nextInt();
        g = new ArrayList[n];
        Pair[] edges = new Pair[n - 1];
        for (int i = 0; i < n; i++)
            g[i] = new ArrayList<>();
        for (int i = 0; i < n - 1; i++) {
            int x = in.nextInt() - 1;
            int y = in.nextInt() - 1;
            g[x].add(y);
            g[y].add(x);
            buity[x][y] = 1;
            buity[y][x] = 1;
            edges[i] = new Pair(x, y);
        }

        boolean ok = true;
        int m = in.nextInt();
        ArrayList<Integer>[] paths = new ArrayList[m];
        int[] gs = new int[m];
        for (int i = 0; i < m; i++) {
            int a = in.nextInt() - 1;
            int b = in.nextInt() - 1;
            int z = in.nextInt();
            gs[i] = z;
            ArrayDeque<Integer> q = new ArrayDeque<>();
            q.add(a);
            int[] p = new int[n];
            Arrays.fill(p, -1);
            p[a] = a;
            while (!q.isEmpty()) {
                int v = q.poll();
                for (int u : g[v]) {
                    if (p[u] == -1) {
                        p[u] = v;
                        q.add(u);
                    }
                }
            }
            paths[i] = new ArrayList<>();
            while (b != a) {
                paths[i].add(b);
                int prev = b;
                b = p[b];
                if (buity[prev][b] < z) {
                    buity[prev][b] = z;
                    buity[b][prev] = z;
                }
            }
            paths[i].add(a);
        }

        for (int i = 0; i < m; i++) {
            int min = 1000000;
            for (int j = 1; j < paths[i].size(); j++) {
                int x = paths[i].get(j - 1);
                int y = paths[i].get(j);
                min = Math.min(min, buity[x][y]);
            }
            ok &= min == gs[i];
        }

        if (ok) {
            for (int i = 0; i < n - 1; i++)
                out.print(buity[edges[i].a][edges[i].b] + " ");
        } else {
            out.println(-1);
        }
    }



    class Pair implements Comparable<Pair> {

        int a;
        int b;

        Pair(int a, int b) {
            this.a = a;
            this.b = b;
        }

        public int compareTo(Pair p) {
            if (a != p.a)
                return Integer.compare(a, p.a);
            else
                return Integer.compare(-b, -p.b);
        }
    }

    class Item {

        int a;
        int b;
        int c;

        Item(int a, int b, int c) {
            this.a = a;
