		x = x * x % mod;
		y /= 2;
	}return r;
}
ll nCr(int r,int n){return F[n] * invF[r] %mod * invF[n-r] % mod; }

ll calc(int s,int e){

	int L , R;
	for( L = s ; a[L] == 0 && L <= e ; L ++ );
	for( R = e ; a[R] == 0 && R >= s ; R -- );

	if( L == e + 1 )return two[ e - s ];

	ll r = 0;


	if( L == R && a[L] == 1)return nCr( L-s, L-s + e-R );

	if( a[L] >= a[R] ){
		int ns = L + 1;
		int ne = ns + a[L] - 2;

		r = (r + calc( ns , ne ) * nCr( L-s , L-s + e - R - a[L] + 2) ) % mod;
	}

	if( a[L] <= a[R] ){
		int ne = R - 1;
		int ns = ne - a[R] + 2;

		r = ( r + calc( ns , ne ) * nCr( L-s , L-s + e - R - a[R] + 2 ) ) % mod;
	}

