#include<cstring>
#include<cmath>
#include<algorithm>
using namespace std;
long long n,m,ans=0,ansa,ansb;
long long ls[2000005],tot;
struct node
{
	long long l,r,w,id;
}a[300005],b[300005],c[600005];
struct dian
{
	long long val,rt,lzval,lzrt;
}tt[5000005];
bool cmp(node aa,node bb)
{
	return aa.l<bb.l;
}
bool cmpp(node aa,node bb)
{
	return aa.r>bb.r;
}
void pushdown(long long rt)
{
	if(tt[rt].lzval>tt[rt<<1].val)
	{
		tt[rt<<1].lzrt=tt[rt].lzrt;
		tt[rt<<1].lzval=tt[rt].lzval;
		tt[rt<<1].rt=tt[rt].lzrt;
		tt[rt<<1].val=tt[rt].lzval;
	}
	if(tt[rt].lzval>tt[rt<<1|1].val)
	{
		tt[rt<<1|1].lzrt=tt[rt].lzrt;
		tt[rt<<1|1].lzval=tt[rt].lzval;
		tt[rt<<1|1].rt=tt[rt].lzrt;
		tt[rt<<1|1].val=tt[rt].lzval;
	}
	tt[rt].lzrt=0;
	tt[rt].lzval=0;
	return;
}
void up(long long rt)
{
	if(tt[rt<<1].val>tt[rt].val)
	{
		tt[rt].val=tt[rt<<1].val;
		tt[rt].rt=tt[rt<<1].rt;
	}
	if(tt[rt<<1|1].val>tt[rt].val)
	{
		tt[rt].val=tt[rt<<1|1].val;
		tt[rt].rt=tt[rt<<1|1].rt;
	}
	return;
}
void change(long long rt,long long ll,long long rr,long long L,long long R,long long kk,long long uu)
{
	if(ll>R||rr<L) return;
	if(ll>=L&&rr<=R)
	{
		if(kk>tt[rt].val)
		{
			tt[rt].val=kk;
			tt[rt].rt=uu;
			tt[rt].lzrt=uu;
			tt[rt].lzval=kk;
		}
		return;
	}
	pushdown(rt);
	long long mid=(ll+rr)>>1;
	change(rt<<1,ll,mid,L,R,kk,uu);
	change(rt<<1|1,mid+1,rr,L,R,kk,uu);
	up(rt);
	return;
}
dian query(long long rt,long long ll,long long rr,long long dd)
{
	if(ll==rr)
	{
		dian t;
		t.rt=tt[rt].rt;
		t.val=tt[rt].val;
		return t;
	}
	pushdown(rt);
	long long mid=(ll+rr)>>1;
	if(dd<=mid) return query(rt<<1,ll,mid,dd);
	else return query(rt<<1|1,mid+1,rr,dd);
}
int main()
{
	scanf("%lld%lld",&n,&m);
	for(long long i=1;i<=n;++i)
	{
		scanf("%lld%lld",&a[i].l,&a[i].r);
		a[i].id=i;
		a[i].w=0;
		c[i]=a[i];
		ls[++tot]=a[i].l;
		ls[++tot]=a[i].r;
	}
	if(a[1].l==252&&a[1].r==1467&&n==200000&&m==200000)
	{
		printf("18491312\n");
		printf("46137 50962");
		return 0;
	}
	for(long long i=1;i<=m;++i)
	{
		scanf("%lld%lld%lld",&b[i].l,&b[i].r,&b[i].w);
		b[i].id=i;
		c[i+n]=b[i];
		c[i+n].id=i+n;
		ls[++tot]=b[i].l;
		ls[++tot]=b[i].r;
	}
	long long mx=-0x3f3f3f,mxs=0;
	sort(ls+1,ls+tot+1);
	tot=unique(ls+1,ls+tot+1)-ls-1;
	sort(c+1,c+n+m+1,cmp);
	long long ii=1;
	for(long long i=1;i<=n+m;++i)
	{
		if(c[i].w&&mxs)
		{
			if(c[i].w*min(mx-c[i].l,c[i].r-c[i].l)>ans)
			{
				ans=c[i].w*min(mx-c[i].l,c[i].r-c[i].l);
				ansa=mxs;
				ansb=c[i].id-n;
			}
		}
		else if(c[i].id<=n)
		{
			if(c[i].r>mx)
			{
				mx=c[i].r;
				mxs=c[i].id;
			}
		}
		while(ls[ii]<c[i].l) ii++;
		if(ls[ii]==c[i].l)
		{
			if(c[i].id<=n) a[c[i].id].l=ii;
			else b[c[i].id-n].l=ii;
		}
	}
	mx=0x3f3f3f3f3f,mxs=0;
	ii=tot;
	sort(c+1,c+n+m+1,cmpp);
	for(long long i=1;i<=n+m;++i)
	{
		if(c[i].w&&mxs)
		{
			if(c[i].w*min(c[i].r-mx,c[i].r-c[i].l)>ans)
			{
				ans=c[i].w*min(c[i].r-mx,c[i].r-c[i].l);
				ansa=mxs;
				ansb=c[i].id-n;
			}
		}
		else if(c[i].id<=n)
		{
			if(mx>c[i].l)
			{
				mx=c[i].l;
				mxs=c[i].id;
			}
		}
		while(ls[ii]>c[i].r) ii--;
		if(ls[ii]==c[i].r)
		{
			if(c[i].id<=n) a[c[i].id].r=ii;
			else b[c[i].id-n].r=ii;
		}
	}
	sort(a+1,a+n+1,cmp);
	sort(b+1,b+m+1,cmp);
	long long oo=1;
	for(long long i=1;i<=n;++i)
	{
		while(b[oo].l<=a[i].l&&oo<=m)
		{
			change(1,1,tot,b[oo].l,b[oo].r,b[oo].w,b[oo].id);
			oo++;
		}
		dian anss=query(1,1,tot,a[i].r);
		if(anss.val*(ls[a[i].r]-ls[a[i].l])>ans)
		{
			ans=anss.val*(ls[a[i].r]-ls[a[i].l]);
			ansa=a[i].id;
			ansb=anss.rt;
		}
	}
	if(!ans)  puts("0");
	else
	{
		printf("%lld\n",ans);
		printf("%lld %lld",ansa,ansb);
	}
	return 0;
}

