int tot1, tot2;
void mv1(int x, int y, int xx, int yy, int val)
{
	cnt[val][x][y]--, cnt[val][xx][yy]++;
	ans1[++tot1] = mp(mp(x, y), mp(xx, yy));
}
void mv2(int x, int y, int xx, int yy, int val)
