{
	tot1 = tot2 = 0;
	for (int i = 1; i <= n; i++)
	{
		int nxt = i % n + 1, q0q = 0, q1q = 0;
		for (int k = l[i][1] - 1; k >= 0; k--)
			if (st[i][1][k] == '0') mv1(i, 1, nxt, 1, 0), q0q++;
			else mv1(i, 1, i, m, 1);
		for (int k = l[i][m] - 1; k >= 0; k--)
			if (st[i][m][k] == '0') mv1(i, m, i, 1, 0);
			else mv1(i, m, nxt, m, 1), q1q++;
		for (int j = 2; j < m; j++)
			for (int k = l[i][j] - 1; k >= 0; k--)
				if (st[i][j][k] == '0') mv1(i, j, i, 1, 0);
				else mv1(i, j, i, m, 1);
	}
	for (int i = 1; i <= n; i++)
	{
		int nxt = i % n + 1, q0q = 0, q1q = 0;
		for (int k = 0; k < L[i][1]; k++)
			if (ed[i][1][k] == '0') mv2(i, 1, nxt, 1, 0), q0q++;
			else mv2(i, 1, i, m, 1);
		for (int k = 0; k < L[i][m]; k++)
			if (ed[i][m][k] == '0') mv2(i, m, i, 1, 0);
			else mv2(i, m, nxt, m, 1), q1q++;
		for (int j = 2; j < m; j++)
			for (int k = 0; k < L[i][j]; k++)
				if (ed[i][j][k] == '0') mv2(i, j, i, 1, 0);
				else mv2(i, j, i, m, 1);
	}
	for (int i = 1; i < n; i++)
	{
		while (cnt[0][i][1] > Cnt[0][i][1]) mv1(i, 1, i + 1, 1, 0);
		while (cnt[1][i][m] > Cnt[1][i][m]) mv1(i, m, i + 1, m, 1);
	}
	for (int i = n; i > 1; i--)
	{
		while (cnt[0][i][1] > Cnt[0][i][1]) mv1(i, 1, i - 1, 1, 0);
		while (cnt[1][i][m] > Cnt[1][i][m]) mv1(i, m, i - 1, m, 1);
	}
