		{
			cin >> st[i][j];
			l[i][j] = st[i][j].size();
			for (int k = 0; k < l[i][j]; k++)
				if (st[i][j][k] == '0') cnt[0][i][j]++;
				else cnt[1][i][j]++;
		}
	for (int i = 1; i <= n; i++)
