        gdsc.colocalisation.cda.engine.CalculationResult result = results.get(i);
        distances[i] = result.distance;
        m1Values[i] = result.m1;
        m2Values[i] = result.m2;
        rValues[i] = result.r;
        if ((distances[i]) > (randomRadius)) {
            indexDistance.add(i);
        }
    }
