            cp.setPixels(mergedSegmentedDisplacementIP.getPixels(n));
            cp.setPixels(1, segmented2RGBIP.getProcessor(n).toFloat(1, null));
        }
    }
    createDisplayImages(channel1RGBIP, channel2RGBIP, segmented1RGBIP, segmented2RGBIP, mergedChannelIP, mergedSegmentedRGBIP, mergedChannelDisplacementIP, mergedSegmentedDisplacementIP);
    updateImage(channel1RGB, gdsc.colocalisation.cda.CDA_Plugin.channel1RGBTitle, channel1RGBIP, showChannel1RGB);
    updateImage(channel2RGB, gdsc.colocalisation.cda.CDA_Plugin.channel2RGBTitle, channel2RGBIP, showChannel2RGB);
