#include <bits/stdc++.h>
using namespace std;

class FenwickTree {
public:
    vector<int> tree; int size;
    explicit FenwickTree(int n) {
        size = n;
        tree = vector<int>((unsigned)n);
    }
    int query(int pos) {
        int ans = 0;
        for (int i = pos; i >= 1; i -= (i & (-i))){
            ans += tree[i];
        } return ans;
    }
    void increment(int pos, int val){
        for (int i = pos; i <= size; i += (i & (-i)))
            tree[i] += val;
    }
};

int main(){
    /** Segment 1: We take the input and store it in a vector of vectors, each single vector contains
     * the cyclic difference in x,y-coordinates of the points of that single polygon. */
    int n; cin >> n;
    vector<int> cumulative_count((unsigned)n+1);
    vector<vector<pair<int,int>>> xy_delta((unsigned)n+1); // Jagged vector of vectors
    vector<vector<pair<int,int>>> polygon((unsigned)n+1); // Jagged vector of vectors
    for (int i = 1; i <= n; i++){
        int k; cin >> k; polygon[i].resize((unsigned)k);
        for (int j = 0; j < k; j++) cin >> polygon[i][j].first >> polygon[i][j].second;
        for (int j = 0; j < k; j++) {
            int x = polygon[i][j].first - polygon[i][(j - 1 + k) % k].first;
            int y = polygon[i][j].second - polygon[i][(j - 1 + k) % k].second;
            int gcd = __gcd(abs(x), abs(y));
            xy_delta[i].emplace_back(x / gcd, y / gcd);
        } cumulative_count[i] = cumulative_count[i - 1] + (int) xy_delta[i].size();
    }
    /** Segment 2: Piling up all the input queries in a jagged vector grouped by right limit of query. */
    int q; cin >> q; vector<vector<pair<int,int>>> queries((unsigned)q+1);
    for (int i = 1; i <= q; i++){
        int query_L, query_R; cin >> query_L >> query_R;
        queries[query_R].emplace_back(query_L, i);
    }

    /** Segment 3: The meat of the logic. Using a fenwick tree to keep tabs on the unique values of x,y-diff
     * and for each point. Then answering the queries as a count of the number of unique x,y-diff in range. */
    map<pair<int, int>, int> xy_diff;
    vector<int> answer((unsigned)q+1);
    FenwickTree tree(cumulative_count[n] + 1);
    for (int i = 1; i <= n; i++){
        for (int j = 0; j < xy_delta[i].size(); j++) {
            // If the x,y-difference we see has not appeared here before, then mark it as appeared and
            // INCREASE VALUE OF CURRENT POINT BY 1. If it has appeared before, invalidate the place where it appeared
            // previously and count it here. This is why we process queries in increasing R-limit.
            if (xy_diff.find(xy_delta[i][j]) != xy_diff.end())
                tree.increment(xy_diff[xy_delta[i][j]], -1);
            xy_diff[xy_delta[i][j]] = cumulative_count[i - 1] + j + 1;
            tree.increment(cumulative_count[i - 1] + j + 1, 1);
        }
        for (auto &j : queries[i]) {
            // Unpacking the Query list and querying sum of points in polygons in range [l,r).
            int l = j.first, r = i, id = j.second;
            answer[id] = tree.query(cumulative_count[i]) - tree.query(cumulative_count[l-1]);
        }
    }
    /** Segment 4: Take the answer and output it. */
    for (int i = 1; i <= q; i++) cout << answer[i] << "\n";
}

