#include<cmath>
#define M 1003
#define N 201000
#define ll long long
using namespace std;

int read() {
	int nm = 0, f = 1;
	char c = getchar();
	for(; !isdigit(c); c = getchar()) if(c == '-') f = -1;
	for(; isdigit(c); c = getchar()) nm = nm * 10 + c - '0';
	return nm * f;
}
ll f[M][M], sum[M][M];
int a[N], b[N];
int belong[N];
int id[N], zd[N];
int n, m, biao;
int ln[M], rn[M];
set<int> st;

int sta[M], tp;
int main() {
	//freopen(".in", "r", stdin), freopen(".out", "w", stdout);
	n = read(), m = read();
	for(int i = 1; i <= n; i++) a[i] = read(), id[a[i]] = i;
	for(int i = 1; i <= n; i++) b[i] = read(), zd[b[i]] = i;
	if(n <= -300) {
		for(int i = 1; i <= m; i++) {
			int op = read();
			if(op == 1) {
				int aa = read(), bb = read(), c = read(), d = read();
				st.clear();
				for(int j = aa; j <= bb; j++) st.insert(a[j]);
				int ans = 0;
				for(int j = c; j <= d; j++) if(st.find(b[j]) != st.end()) ans++;
				cout << ans << "\n";
			} else {
				int aa = read(), bb = read();
				swap(b[aa], b[bb]);
			}
		}
		return 0;
	}
	biao = sqrt(n);
	for(int i = 1; i < M; i++) ln[i] = 0x3e3e3e3e, rn[i] = 0;
	for(int i = 1; i <= n; i++) belong[i] = (i - 1) / biao + 1;
	for(int i = 1; i <= n; i++) {
		int op = id[b[i]];
		int zz = belong[op];
		f[zz][belong[i]]++;
	}
	for(int i = 1; i <= belong[n]; i++) {
		for(int j = 1; j <= belong[n]; j++) {
			sum[i][j] = sum[i - 1][j] + f[i][j];
		}
	}
	while(m--) {
		int op = read();
		int ans = 0;
		if(op == 1) {
			int aa = read(), bb = read(), c = read(), d = read();
			for(int i = aa; i <= min(belong[aa] * biao, bb); i++) {
				if(zd[a[i]] >= c && zd[a[i]] <= d) ans++;
			}
			if(belong[aa] != belong[bb]) {
				for(int i = (belong[bb] - 1) * biao + 1; i <= bb; i++)
					if(zd[a[i]] >= c && zd[a[i]] <= d) ans++;
			}
			int ln = belong[aa], rn = belong[bb] - 1;

			for(int i = c; i <= min(belong[c] * biao, d); i++) if(id[b[i]] >= belong[aa] * biao + 1 && id[b[i]] <= (belong[bb] - 1) * biao) ans++;
			if(belong[c] != belong[d]) {
				for(int i = (belong[d] - 1) * biao + 1; i <= d; i++)
					if(id[b[i]] >= belong[aa] * biao + 1 && id[b[i]] <= belong[bb - 1] * biao) ans++;
			}
