			f[zz][belong[aa]]++;
			op = id[b[bb]], zz = belong[op];
			f[zz][belong[bb]]++;
			aa = belong[aa], bb = belong[bb];
			for(int i = 1; i <= belong[n]; i++) sum[i][aa] = sum[i - 1][aa] + f[i][aa], sum[i][bb] = sum[i - 1][bb] + f[i][bb];

		}
	}
	return 0;
}

