{
    int res = 1;     // Initialize result  
    if (x >= p)
        c++;
    x = x % p; // Update x if it is more than or  
                // equal to p 

    if (x == 0) return 0; // In case x is divisible by p; 

    while (y > 0)
    {
        // If y is odd, multiply x with result  
        if (y & 1)
        {
            if (res * x >= p)
                c++;
            res = (res * x) % p;
