                }
            }
            eds[ed+1].push_back(i);
           // byebye[ed].push_back(tem);
        }
    }
}

void solve()
{
    sort(xx+1,xx+1+cnt1);
    cnt1=unique(xx+1,xx+1+cnt1)-xx-1;
    sort(yy+1,yy+1+cnt2);
    cnt2=unique(yy+1,yy+1+cnt2)-yy-1;
    processvv();
    processjoin();

    for(int i=1;i<=cnt1;i++)
    {
        for(int j=0;j<eds[i].size();j++)
        {
            int theone=eds[i][j];
            int st=Hash1(rec[theone][0]);
            int ed=Hash1(rec[theone][2]);
            if(st>ed)
            {
                swap(st,ed);
            }
            int hh=Hash2(rec[theone][1]);
            for(int jj=st;jj<=ed;jj++)
            {
                for(int k=0;k<vv[jj].size();k++)
                {
                   // add(vv[j][k],hh);
                    sub(vv[jj][k],hh);
                }
            }
        }
        for(int j=0;j<vv[i].size();j++)
        {
            int YUNow=rec[vv[i][j]][1];
            int YDNow=rec[vv[i][j]][3];
            if(YUNow<YDNow)
            {
                swap(YUNow,YDNow);
            }
            for(int k=1;k<i;k++)
            {
                for(int kk=0;kk<vv[k].size();kk++)
                {
                    int YUPre=rec[vv[k][kk]][1];
                    int YDPre=rec[vv[k][kk]][3];
                    if(YUPre<YDPre)
                    {
                        swap(YUPre,YDPre);
                    }
                    int YUT=min(YUNow,YUPre);
                    int YDT=max(YDNow,YDPre);
                    if(YUT>YDT)
                    {
                        int num=qq(vv[k][kk],Hash2(YDT),Hash2(YUT));
                        ans+=(ll)num*((ll)num-1)/2;
                    }
                }
            }
        }

    }
    printf("%lld\n",ans);
}
int main()
{

//    freopen("c://duipai//data.txt","r",stdin);
//    freopen("c://duipai//wa.txt","w",stdout);
    while(~scanf("%d",&n))
    {
        init();
//        string str;
//        cin>>str;
//        cout<<str<<endl;
        for(int i=1;i<=n;i++)
        {
            int x1,y1,x2,y2;
            scanf("%d %d %d %d",&x1,&y1,&x2,&y2);
            rec[i][0]=x1;
            rec[i][1]=y1;
            rec[i][2]=x2;
            rec[i][3]=y2;
            xx[++cnt1]=x1;
            xx[++cnt1]=x2;
            yy[++cnt2]=y1;
            yy[++cnt2]=y2;
        }
        solve();

    }
}
