#include <bits/stdc++.h>
using namespace std;

const int MN = 100005;
const int MX = 1005;

int N, M, K;
int Cn;

struct data {
	int u, v;
	int color;
	data(int u = 0, int v = 0) : u(u), v(v), color(-1) {}
	int Find(int uu) {
		return uu == u ? v : u;
	}
} edge[MN];

vector<int> con[2 * MX];

bool vis[2 * MX];
int col[2 * MX], coln, cvis[2 * MX];
int bf[2 * MX];

int cvisn = 0;
void dfs(int u) {
	vis[u] = true;
	cvisn++;
	for (auto it : con[u]) if (edge[it].color != -1) cvis[edge[it].color] = bf[edge[it].color] = cvisn;
	int cnt = 0;
	for (auto it : con[u]) if (edge[it].color == -1) {
		int v = edge[it].Find(u);
		for (auto iit : con[v]) if (edge[iit].color != -1) cvis[edge[iit].color] = cvisn;
		int flg = 0;
		for (int i = 1; i <= Cn; i++) if (cvis[i] != cvisn) {
			edge[it].color = i;
			cvis[i] = bf[i] = cvisn;
			flg = 1;
			break;
		}
		if (!flg) edge[it].color = ++Cn, cvis[Cn] = bf[Cn] = cvisn;
		for (auto iit : con[v]) if (edge[iit].color != -1) cvis[edge[iit].color] = bf[edge[iit].color];
	}
	for (auto it : con[u]) {
		int v = edge[it].Find(u);
		if (!vis[v]) dfs(v);
	}
}

int main() {

//	freopen("in.txt", "r", stdin);
//	freopen("out.txt", "w", stdout);

	scanf("%d%d%d", &N, &M, &K);
	for (int i = 0; i < K; i++) {
		int u, v; scanf("%d%d", &u, &v);
		con[u].push_back(i);
		con[v + MX].push_back(i);
		edge[i] = data(u, v + MX);
	}

	int id[MX];
	for (int i = 1; i <= N; i++) id[i] = i;

	start:
	random_shuffle(id + 1, id + N);

	for (int i = 1; i <= N; i++) if (!vis[id[i]]) {
		dfs(id[i]);
	}

	int Max = 0;
	for (int i = 0; i < 2 * MX; i++) Max = max(Max, (int)con[i].size());
	if (Max != Cn) goto start;

	printf("%d\n", Cn);
	for (int i = 0; i < K; i++) printf("%d%c", edge[i].color, " \n"[i == K - 1]);

	return 0;

}

