struct Point
{
    double x, y;

    Point() {}
    Point(double x, double y): x(x), y(y) {}

    friend Point const operator -(Point const &p, Point const &q)
    {
        return Point(p.x - q.x, p.y - q.y);
    }

    friend Point const operator +(Point const &p, Point const &q)
    {
        return Point(p.x + q.x, p.y + q.y);
    }

    friend Point const operator *(double const t, Point const &p)
    {
        return Point(p.x * t, p.y * t);
    }

    friend double dot(Point const &p, Point const &q)
    {
        return p.x * q.x + p.y * q.y;
    }

    friend double sqr(Point const &p)
    {
        return dot(p, p);
    }

    friend double calcLength(Point const &p)
    {
        double const squaredLength = sqr(p);
        assert(squaredLength >= 0);
        return sqrt(squaredLength);
    }

    friend Point const normalize(Point const &p)
    {
        double const length = calcLength(p);
        assert(length > 0);
        return (1 / length) * p;
    }

    friend Point const rotateCcw(Point const &p, double const a)
    {
        double const c = cos(a);
        double const s = sin(a);
        return Point(p.x * c - p.y * s, p.x * s + p.y * c);
    }

    friend istream &operator >>(istream &in, Point &p)
    {
        return in >> p.x >> p.y;
    }

    friend ostream &operator <<(ostream &out, Point const &p)
    {
        return out << p.x << " " << p.y;
    }
};


Point const calcV(Point const &w, Point d, double const v)
{
    d = normalize(d);
    double const discr = sqr(dot(w, d)) + sqr(v) - sqr(w);
    assert(discr >= 0);
    return (dot(w, d) + sqrt(discr)) * d;
}


double const EPS = 1e-9;

Point from, to;
double vMax, t;
Point w1, w2;


void trySimpleSolution()
{
    Point const v(calcV(w1, to - from, vMax));
    if (sqr(t * v) >= sqr(to - from)) {
        cout << calcLength(to - from) / calcLength(v) << "\n";
        exit(0);
    }
}


double calcTime(Point const &d, double const vLen)
{
    Point const u(calcV(w1, d, vLen));
    Point const p(from + t * u);
    Point const v(calcV(w2, to - p, vMax));
    double const time = t + calcLength(to - p) / calcLength(v);
    return time;
}


typedef pair<double, double> pdd;

template<class F>
pdd const findMin(F f, double a, double b)
{
    while (b - a >= EPS) {
        double const l = a + (b - a) / 3;
        double const r = b - (b - a) / 3;
        if (f(l) > f(b)) {
            a = l;
        } else {
            b = r;
        }
    }
    return mp((a + b) / 2, f((a + b) / 2));
}


struct Velocity
{
    Point d;
    Velocity(Point const &d): d(d) {}

    double operator ()(double const v) const
    {
        return calcTime(d, v);
    }
};


struct Angle
{
    Point d;
    Angle(Point const &d): d(d) {}

    double operator ()(double const a) const
    {
        Point const cd(rotateCcw(d, a));
        //pdd const ans(findMin(Velocity(cd), 0, vMax));
        //cerr << "  v: (" << ans.fst << " -> " << ans.snd << "); " << cd << "\n";
        //return ans.snd;
        return calcTime(cd, vMax);
    }
};


int main()
{
    cin >> from >> to;
    cin >> vMax >> t;
    cin >> w1 >> w2;

    cout.precision(12);
    cout << fixed;

    trySimpleSolution();

    pdd const ans1(findMin(Angle(to - from), 0, M_PI));
    //cerr << "a1: (" << ans1.fst << " -> " << ans1.snd << "); " << to - from << "\n";

    pdd const ans2(findMin(Angle(from - to), 0, M_PI));
    //cerr << "a2: (" << ans2.fst << " -> " << ans2.snd << "); " << from - to << "\n";

    cout << min(ans1.snd, ans2.snd) << "\n";

    return 0;
}

