#define MAXN 1000000

struct edge {
    int endp;
    ll len;
};

int n, m;
ll l[MAXN];
ll dis[MAXN];
vector<edge> children[MAXN];
vll arr[MAXN], pref[MAXN];

vll merge(vll a, vll b) {
    vll res;
    int i = 0, j = 0;
    while(i < sz(a) || j < sz(b)) {
        if(i == sz(a) || (j < sz(b) && b[i] < a[i])) res.PB(b[j++]);
        else res.PB(a[i++]);
    }
    return res;
}

void dfs(int node) {
    arr[node].PB(dis[node]);
    for (const edge e : children[node]) {
        dis[e.endp] = dis[node] + e.len;
        dfs(e.endp);
        arr[node] = merge(arr[node], arr[e.endp]);
    }
    ll curr = 0;
    for (const ll a : arr[node]) {
        curr += a;
        pref[node].PB(curr);
    }
}

int main() {
    ios::sync_with_stdio(0);
    cin.tie(0);
    cin >> n >> m;
    FOR(i, 1, n) {
        cin >> l[i];
        children[(i + 1) / 2 - 1].PB({i, l[i]});
    }
    dfs(0);
    F0R(i, m) {
        int curr;
        ll h;
        ll res = 0;
        cin >> curr >> h;
        curr--;
        int prev = -1;
        while(h > 0 && curr != -1) {
            res += h;
            for (const edge e : children[curr]) if(e.endp != prev) {
                int idx = UB(all(arr[e.endp]), h + dis[curr]) - arr[e.endp].begin();
                if (idx) {
                    res += (h + dis[curr]) * idx - pref[e.endp][idx - 1];
                }
            }
            h -= l[curr];
            prev = curr;
            curr = (curr + 1) / 2 - 1; 
        }
        cout << res << "\n";
    }
}

