int n, a[5001], dp[5001][5001];
int main() {
    ios_base::sync_with_stdio(false);
    cin >> n;
    for (int i = 0; i < n; ++i) 
        cin >> a[i];

    int f = 0;
    for (int i = 0; i < n; ++i)
        for (int j = i + 1; j < n; ++j)
            f += (a[i] > a[j]);


    dp[0][0] = 0;
    for (int i = 1; i < n; ++i)
        dp[0][i] = (a[0] < i);
    for (int i = 1; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            dp[i][j] = dp[i - 1][j] + (a[i] < j);
        }
    }

    int best = 10000, howMany = 0;
    for (int i = 0; i < n - 1; ++i) {
        for (int j = i + 1; j < n; ++j) {
            if (a[i] <= a[j]) continue;
            int ans = f;
            ans = f + 2 * (dp[i][a[i]] + dp[j][a[j]] - dp[i][a[j]] - dp[j][a[i]]) + 1;
            if (ans < best) {
                best = ans;
                howMany = 1;
            }
            else if (ans == best) howMany++;

        }
    }
    cout << best << " " << howMany << '\n';






}

