void trace_() {cerr << "\n";}
template<typename T1, typename... T2> void trace_(T1 t1, T2... t2) {cerr << ' ' << t1; trace_(t2...); }
#define trace(...) cerr << #__VA_ARGS__ << " :", trace_(__VA_ARGS__);


void solve() {
    int n, m; cin >> n >> m;
    vector<vector<int>> G(n, vector<int> (m));
    for (int i = 0; i < n; i++) for (int j = 0; j < m; j++) {
        cin >> G[i][j];
    }

    int dx[] = {0, 1, 0, -1}, dy[] = {-1, 0, 1, 0};
    auto valid = [&](int nx, int ny) -> bool {
        if (nx < 0 || ny < 0 || nx >= n || ny >= m) return false;
        return true;
    };
    auto is_bad = [&](int x, int y) -> bool {
        if (G[x][y] == 1) return false;


        for (int i = 0; i < 4; i++) {
            int nx = x + dx[i], ny = y + dy[i];
            if (!valid(nx, ny)) continue;
            if (G[nx][ny] < G[x][y]) {
                return false;
            }
        }
        return true;
    };

    vector<pair<int, int>> bad;
    for (int i = 0; i < n; i++) for (int j = 0; j < m; j++) {
        if (is_bad(i, j)) {
            bad.push_back({i, j});
        }
    }

    if (bad.size() == 0) {
        cout << 0 << "\n";
    } else if (bad.size() > 10) {
        cout << 2 << "\n";
    } else {
        int cnt = 0;
        bad.push_back({0, 0});
        for (int i = 0; i < bad.size(); i++) {
            for (int x2 = 0; x2 < n; x2++) for (int y2 = 0; y2 < m; y2++) {
                int x1, y1;
                tie(x1, y1) = bad[i];
                if (x1 == x2 && y1 == y2) continue;
                swap(G[x1][y1], G[x2][y2]);
                bool ok = true;
                if (is_bad(x1, y1) || is_bad(x2, y2)) ok = false;
                for (auto &p : bad) {
                    int nx, ny;
                    tie(nx, ny) = p;
                    if (is_bad(nx, ny)) {
                        ok = false;
                    }
                }
                for (int j = 0; j < 4; j++) {
                    int nx = x1 + dx[j], ny = y1 + dy[j];
                    if (valid(nx, ny) && is_bad(nx, ny)) ok = false;
                    nx = x2 + dx[j], ny = y2 + dy[j];
                    if (valid(nx, ny) && is_bad(nx, ny)) ok = false;
                }
                if (ok) {
                    // trace(G[x1][y1], G[x2][y2]);
                    cnt++;
                }
                swap(G[x1][y1], G[x2][y2]);
            }
        }
        if (cnt > 0) {
            cout << 1 << " " << cnt << "\n";
        } else {
            cout << 2 << "\n";
        }
    }

}

int main() {
    ios_base::sync_with_stdio(false); cin.tie(0);
    solve();
}

