private org.talend.repository.model.RepositoryNode getFolder(org.talend.core.model.repository.ERepositoryObjectType currentType, java.lang.String path, java.util.List<org.talend.repository.model.IRepositoryNode> rootNodes) {
    if ((path == null) || (path.isEmpty())) {
        return null;
    }
    org.talend.repository.model.RepositoryNode folderNode = null;
    java.lang.String folderName;
    java.lang.String remainingPath = path;
    if (path.contains("/")) {
        folderName = path.substring(0, path.indexOf("/"));
        remainingPath = path.substring(((path.indexOf("/")) + 1));
    }else {
        folderName = path;
        remainingPath = null;
    }
    for (org.talend.repository.model.IRepositoryNode node : rootNodes) {
        if (((node.getType()) == (ENodeType.SIMPLE_FOLDER)) && (node.getProperties(EProperties.CONTENT_TYPE).equals(currentType))) {
            if (node.getProperties(EProperties.LABEL).equals(folderName)) {
                folderNode = ((org.talend.repository.model.RepositoryNode) (node));
                break;
            }
            if (((remainingPath != null) && ((node.getObject().getPath()) != null)) && (!(node.getObject().getPath().isEmpty()))) {
                java.lang.String fullPath = ((node.getObject().getPath()) + "/") + (node.getProperties(EProperties.LABEL));
                if (fullPath.equals(path)) {
                    folderNode = ((org.talend.repository.model.RepositoryNode) (node));
                    remainingPath = null;
                }else
                    if (path.startsWith(fullPath)) {
                        folderNode = ((org.talend.repository.model.RepositoryNode) (node));
                        remainingPath = path.substring(((fullPath.length()) + 1));
                    }

                break;
            }
        }
    }
    if (folderNode != null) {
        if (remainingPath == null) {
            return folderNode;
        }
        return getFolder(currentType, remainingPath, folderNode.getChildren());
    }
    return null;
}

