#define Pro "tmp"
#define maxN 5007

vii adj[maxN], rev[maxN];
vi trace;

bool visited[maxN];
ll f[maxN][maxN];

int bf[maxN][maxN];

void toposort(int u) {
    visited[u] = 1;
    fto (i, 0, (int)adj[u].size()-1) {
        int v = adj[u][i].FF;
        if (!visited[v]) toposort(v);
    }

    trace.pb(u);
    return;
}

int main() {
    #ifndef ONLINE_JUDGE
        freopen(Pro".inp", "r", stdin);
        freopen(Pro".out", "w", stdout);
    #endif // ONLINE_JUDGE

    int n, m, t;
    scanf("%d%d%d", &n, &m, &t);

    fto (i, 1, m) {
        int u, v, w;
        scanf("%d%d%d", &u, &v, &w);
        adj[u].pb(mp(v, w));
        rev[v].pb(mp(u, w));
    }

    toposort(1);
    reverse(trace.begin(), trace.end());

    fto (i, 1, n) visited[i] = 0;
    fto (i, 0, (int)trace.size()-1) {
        int u = trace[i];
        visited[u] = 1;

        f[u][1] = 0LL;
        fto (k, 2, n) f[u][k] = OO;

        fto (j, 0, (int)rev[u].size()-1) {
            int v = rev[u][j].FF;
            int w = rev[u][j].SS;

            if (!visited[v]) continue;
            if (v == 1) {
                f[u][2] = min(f[u][2], (ll)w);
                bf[u][2] = 1;
            }
            fto (k, 3, n) {
                if (f[u][k] > f[v][k-1]+w) {
                    f[u][k] = f[v][k-1]+w;
                    bf[u][k] = v;
                }
            }
        }
    }

    int ans = 0;
    int cur = n;

    fto (i, 1, n) {
        if (f[n][i] <= (ll)t) ans = i;
    }

    vi vc;
    fdto (i, ans, 1) {
        vc.pb(cur);
        cur = bf[cur][i];
    }
    reverse(vc.begin(), vc.end());

    cout << ans << endl;
    ftoit (it, vc) cout << (*it) << " ";

    return 0;
}

