template<class RandomIt, class Compare>
RandomIt my_lower_bound(RandomIt begin, RandomIt end, Compare comparator)
{
	auto i = begin;
	auto j = end;

	auto result = end;
	while (i != j)
	{
		auto middle = (j - i) / 2 + i;

		auto cmp = comparator(*middle);
		if (cmp == 0)
		{
			result = middle;
			j = middle;
		}
		else if (cmp == -1)
		{
			j = middle;
		}
		else
		{
			i = middle + 1;
		}
	}
	return result;
}

template<class RandomIt, class Compare>
RandomIt my_upper_bound(RandomIt begin, RandomIt end, Compare comparator)
{
	auto i = begin;
	auto j = end;

	auto result = end;
	while (i != j)
	{
		auto middle = (j - i) / 2 + i;
		auto cmp = comparator(*middle);
		if (cmp == 0)
		{
			i = middle + 1;
			result = i;
		}
		else if (cmp == -1)
		{
			j = middle;
		}
		else
		{
			i = middle + 1;
		}
	}
	return result;
}

template<class RandomIt, class Compare>
std::pair<RandomIt, RandomIt> my_equal_range(RandomIt begin, RandomIt end, Compare cmp)
{
	return std::make_pair(my_lower_bound(begin, end, cmp), my_upper_bound(begin, end, cmp));
}

struct suffix
{
	suffix(std::string const* original, int start)
		: original(original),
		  start(start)
	{
	}

	bool operator<(suffix const & other) const
	{
		return strcmp(original->c_str() + start, original->c_str() + other.start) == -1;
	}

	size_t length() const
	{
		return original->length() - start;
	}

	char operator[](int i) const
	{
		return original->at(start + i);
	}

	bool is_suffix() const
	{
		return start == 0;
	}

private:
	std::string const * original;
	int start;
};


int main()
{
#ifndef ONLINE_JUDGE
	freopen("Source.txt", "rt", stdin);
#endif

	std::string s;
	std::cin >> s;

	std::vector<suffix> suffix_array;
	for (int i = 0; i < s.size(); ++i)
	{
		suffix_array.push_back(suffix(&s, i));
	}

	std::sort(suffix_array.begin(), suffix_array.end());

	int result = -1;

	auto start = suffix_array.begin();
	auto end = suffix_array.end();

	for (int i = 0; i < s.size() - 2; ++i)
	{
		auto ch = s[i];
		auto its = my_equal_range(start, end, [ch, i](suffix const & a) -> int
		{
			if (a.length() < i + 1) return 1;
			if (a[i] == ch) return 0;
			if (a[i] < ch) return 1;
			return -1;
		});

		auto dist = std::distance(its.first, its.second);
		if (dist < 3) break;

		start = its.first;
		end = its.second;

		auto lower_bound = my_lower_bound(start, end, [i](suffix const & a)
		{
			if (a.length() == i + 1) return 0;
			if (a.length() > i + 1) return -1;
			return 1;
		});

		if (lower_bound != end) result = i;
	}

	if (result == -1) std::cout << "Just a legend";
	else std::cout << s.substr(0, result + 1);

	return 0;
}

