#include <bits/stdc++.h>

const int maxn = 3e5 + 10;
const int inf = 0x3f3f3f3f;

template<class Tp> inline void read(Tp& res) {
    res = 0;  char ch = getchar();  bool neg = 0;
    while(!isdigit(ch))
        neg |= ch == '-', ch = getchar();
    while(isdigit(ch)) 
        res = (res << 1) + (res << 3) + (ch & 15), ch = getchar();
    if(neg)
        res = -res; 
}
inline int _min(int a,int b) { return a < b ? a : b; }
inline void cmin(int& a,int b) {
    if(a > b)
        a = b;
}

int n, m, i, j, k, T;  
int a[maxn], b[maxn], c[maxn], pos[maxn], t[maxn << 2];
std::queue<int> q[maxn];  

inline bool check() {
    for(int i = 1;i <= n;i++)
        if(c[i] != 0)
            return 0;
    return 1;    
}

inline void push_up(int u) {
    t[u] = _min(t[u << 1],t[u << 1 | 1]);
}
void build(int l,int r,int u) {
    if(l == r) 
        return t[u] = a[l], void(); 
    int mid = (l + r) >> 1;
    build(l,mid,u << 1);
    build(mid + 1,r,u << 1 | 1);
    push_up(u);  
}
int query(int ql,int qr,int l,int r,int u) {
    if(ql <= l && r <= qr) 
        return t[u];
    int mid = (l + r) >> 1, res = inf;
    if(ql <= mid)
        cmin(res,query(ql,qr,l,mid,u << 1));
    if(mid < qr)
        cmin(res,query(ql,qr,mid + 1,r,u << 1 | 1));
    return res;  
}
void modify(int m,int l,int r,int u,int v) {
    if(l == m && r == m)
        return t[u] = v, void();
    int mid = (l + r) >> 1;
    if(m <= mid)
        modify(m,l,mid,u << 1,v);
    else
        modify(m,mid + 1,r,u << 1 | 1,v);
    push_up(u);        
}

int main() {
    read(T);
    while(T--) {
        read(n);
        memset(c,0,sizeof(c));   
        for(int i = 1;i <= n;i++) {
            read(a[i]);
            c[ a[i] ]++;
            q[ a[i] ].push(i);
        }
        for(int i = 1;i <= n;i++)
            read(b[i]), c[ b[i] ]--;
        if(!check()) {
            puts("NO");
            continue; 
        } 
        for(int i = 1;i <= n;i++) {
            pos[i] = q[ b[i] ].front();
            q[ b[i] ].pop();
        } 
        build(1,n,1);  bool flag = 1;
        for(int i = 1;i <= n;i++) {
            int p = pos[i];
            if(query(1,p,1,n,1) != b[i]) {
                flag = 0;  
                puts("NO");
                break;
            }
            modify(p,1,n,1,inf);
        }
        if(flag)
            puts("YES");
    }
    return 0;
}

