#include<cstdio>
#include<cstring>
#include<utility>
#include<vector>
#include<queue>
#define min(a,b) (a<b?a:b)
using namespace std;
typedef pair<long long,int> P;
const int MAXN=2e5+7;
const long long INF=1e17;
vector<P>G[MAXN];
priority_queue<P,vector<P>,greater<P> >q;
long long  cost[MAXN],d[MAXN];
int done[MAXN],visit[MAXN];
int stack[MAXN],tot=0;
long long dijkstra(int i){
	long long res=cost[i];
	while(!q.empty()){
		P p=q.top();q.pop();
		int u=p.second;
		if(done[u])continue;
		done[u]=1;
		d[u]=p.first;
		if(!visit[u]){
			stack[tot++]=u;
			visit[u]=1;
		}
		int len=G[u].size();
		for(int i=0;i<len;++i){
			int v=G[u][i].second;
			if(d[v]>d[u]+G[u][i].first){
				d[v]=d[u]+G[u][i].first;
				if(!visit[v]){
					stack[tot++]=v;
					visit[v]=1;
				}
				if(2*d[v]+cost[v]<res){
					//printf("d[%d]=%lld\n",v,d[v]);
					res=2*d[v]+cost[v];
					q.push(P(d[v],v));
				}

			}
		}
	}
	return res;
}
void init(){
	while(tot!=0){
		int no=stack[--tot];
		d[no]=INF;
		done[no]=0;
		visit[no]=0;
	}
}
int main(){
	int N,M;scanf("%d%d",&N,&M);
	for(int i=0;i<M;++i){
		int a,b;long long c;scanf("%d%d%lld",&a,&b,&c);
		G[a].push_back(P(c,b));
		G[b].push_back(P(c,a));
	}
	for(int i=1;i<=N;++i)scanf("%lld",&cost[i]);
	for(int i=1;i<=N;++i)d[i]=INF,done[i]=0;
	tot=0;

	for(int i=1;i<=N;++i){
		init();
		while(!q.empty())q.pop();
		q.push(P(0,i));
		printf("%lld ",dijkstra(i));
	}
	return 0;
}

