    for (int i=0; i<g[here].size(); i++) {
        int there = g[here][i].fi;
        if (l >= there || there >= r) continue;
        dfs(there, cnt+1, sumC + g[here][i].se, l, there);
        dfs(there, cnt+1, sumC + g[here][i].se, there, r);
    }
}

