    priority_queue<pair<int,int> > Q;
    num[A-B]=0;
    Q.push(make_pair(0,-A));

    while(!Q.empty()) {
        pair<int,int> P=Q.top();
        Q.pop();
        _P("%d %d\n",P.first,-P.second);
        if(num[-P.second-B] != P.first) continue;
        if(-P.second>B && num[-P.second-1-B] > P.first+1) {
            num[-P.second-1-B] = P.first+1;
            Q.push(make_pair(P.first+1,P.second+1));
        }
        FOR(i,X.size()) {
            y = -P.second - (-P.second)%X[i];
            if(y < B) continue;
            if(num[y-B] > P.first+1) {
                num[y-B] = P.first+1;
                Q.push(make_pair(P.first+1,-y));
            }
        }
    }

    _P("%d\n",num[0]);
    return;
}


