	inline int New(LL w) {
		int x = 0 ;  if(top) x = st[top] ; else x = ++ tot ;
		son[x][0] = son[x][1] = 0 ;
		size[x] = 1 ; pos[x] = rand() ; sum[x] = w ; val[x] = w ; return x ; 
	}
	inline void pushup(int now) {
		size[now] = size[ls] + size[rs] + 1 ;
		sum[now] = sum[ls] + sum[rs] + val[now] ;
	}
	int Merge(int x , int y) {
		if(!x || !y) return x + y ;
		if(pos[x] < pos[y]) {
			son[x][1] = Merge(son[x][1] , y) ;
			pushup(x) ; return x ;
		}
		else {
			son[y][0] = Merge(x , son[y][0]) ;
			pushup(y) ; return y ;
		}
	}
	void Split(int now , LL k , int &x , int &y) {
		if(!now) return (void)(x = y = 0) ;
		if(val[now] <= k) {
			x = now ;
			Split(rs , k , rs , y) ;
		}
		else {
			y = now ;
			Split(ls , k , x , ls) ;
		}
		pushup(now) ;
	}
	inline void Insert(int &root , LL w) {
		int x , y ;
		Split(root , w , x , y) ;
		root = Merge(Merge(x , New(w)) , y) ;
	}
	inline void Del(int &root , LL w) {
		int x , y , z ;
		Split(root , w , x , z) ;
		Split(x , w - 1 , x , y) ;
		st[++top] = y ;
		y = Merge(son[y][0] , son[y][1]) ;
		root = Merge(Merge(x , y) , z) ;
	}
	inline LL Rnk_val(int now , int k) {
		while(1) {
			if(k <= size[ls]) now = ls ;
			else if(k == size[ls] + 1) return val[now] ;
			else k -= size[ls] + 1 , now = rs ;
		}
	}
	inline LL Kth_Sum(int now , int k) { // 找前k大元素的和 
		if(!k) return 0 ;
		LL ret = 0 ;
		while(1) {
			if(k <= size[ls]) now = ls ;
			else if(k == size[ls] + 1) {
				ret += sum[ls] + val[now] ;
				return ret ;
			}
			else {
				ret += sum[ls] + val[now] ;
				k -= size[ls] + 1 ;
				now = rs ;
			}
		}
	}
}
