	while(a){
		if(a%10!=4 && a%10!=7) return false;
		a/=10;
	}
	return true;

}

int makeR(int node,int pre){

	for(int i=0;i<way[node].size();i++)
		if(way[node][i].fi!=pre)
			cntR[node]+=makeR(way[node][i].fi,node);

	return ++cntR[node];

}

void makeL(){

	for(int i=1;i<=N;i++)
		cntL[i] = N-cntR[i]+1;

}

int rec(int node,int pre){

	for(int i=0;i<way[node].size();i++){

		int to = way[node][i].fi;
		int c = way[node][i].se;

		if(pre!=to){
			if(c) R[node]+=cntR[to];
			else R[node]+=rec(to,node);
		}

	}

	return R[node];

}

void f(int node,int pre){

	for(int i=0;i<way[node].size();++i){

		int to = way[node][i].fi;
		int c = way[node][i].se;

		if(to==pre) continue;

		int a1 = (c? cntR[node]-cntR[to]:R[node]-R[to]);
		int a2 = c? cntL[node]-1 : L[node];

		L[to] = a1+a2;

