public class Main {
    static class MyScanner {
        BufferedReader br;
        StringTokenizer st;

        public MyScanner() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine(){
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }

    }

    public static void main(String[] args) {
        MyScanner sc = new MyScanner();
        int t = sc.nextInt();

        while (t-- != 0){
            int n = sc.nextInt();
            int m = sc.nextInt();

            ArrayList <Integer> a1 = new ArrayList<>();
            ArrayList<Integer> a2 = new ArrayList<>();

            int a [] =new int[n];
            for(int i=0;i<n;i++){
                a[i] = sc.nextInt();
            }

            for(int i=0;i<n;i++){
                int x =sc.nextInt();
                if(x == 1){
                    a1.add(a[i]);
                }else{
                    a2.add(a[i]);
                }
            }

            Collections.sort(a1,Collections.reverseOrder());
            Collections.sort(a2,Collections.reverseOrder());

            int j =a1.size()-1;
            int ans =Integer.MAX_VALUE;
            int currPrice = 0;
            int size = 0;
            int i=0;
            for(int z = 0;z<a1.size();z++){
                currPrice += 1;
                size += a1.get(z);
            }
            while(j >= 0){
                while(i < a2.size() && size < m ){
                    currPrice += 2;
                    size += a2.get(i);
                    i++;
                }
                if(size >= m){
                    ans = Math.min(currPrice,ans);
                }
                currPrice -=1;
                size -=  a1.get(j);
                j--;
            }

            if(ans != Integer.MAX_VALUE)System.out.println(ans);
            else System.out.println(-1);

        }
    }
}

