std::string toStr(const std::map<int, int>& m)
{
    std::ostringstream ss;
    for (const auto& it : m) ss << it.first << ":" << it.second << " ";
    return ss.str();
}

void solve3(int n, int k)
{
    std::unordered_map<std::string, int64_t> M;
    for (int i=0;i<n;++i)
    {
        std::cin >> A[i];
        C[i] = extract(A[i], k);
        B[i] = toStr(C[i]);
        M[B[i]]++;
    }
    int64_t result = 0;
    for (int i=0;i<n;++i)
    {
        auto& e = C[i];
        M[B[i]]--;
        for (auto& it : e)
            it.second = k-it.second;
        auto it = M.find(toStr(e));
        if (it != M.end()) result += it->second;
    }
    std::cout << result;
}

int main()
{
    std::ios_base::sync_with_stdio(0);
    int n,k;
    std::cin >> n >> k;
    solve3(n, k);
    return 0;
}

