private static java.util.List<java.lang.String> splitEquation(java.lang.String equation) {
    java.util.List<java.lang.String> list = new java.util.ArrayList<java.lang.String>();
    int currentIndex = 0;
    int index = equation.indexOf("MATH");
    while (index != (-1)) {
        java.lang.String beforeMATH = equation.substring(0, index);
        list.add(beforeMATH);
        int functionOpen = equation.indexOf("{", index);
        if (functionOpen == (-1)) {
            throw new editor.DrawableGroupParserException((((("No open syntax for function at index " + (index + currentIndex)) + " '") + equation) + "'"));
        }
        int functionClose = editor.DrawableGroupParser.getClosingIndex(equation, "{", "}", functionOpen);
        if (functionClose == (-1)) {
            throw new editor.DrawableGroupParserException(("No close syntax for function at index " + (index + currentIndex)));
        }
        java.lang.String math = equation.substring((functionOpen + 1), functionClose).trim();
        editor.EquationScanner2 scan = new editor.EquationScanner2(math);
        while (scan.hasNext()) {
            list.add(scan.next());
        } 
        equation = equation.substring((functionClose + 1)).trim();
        currentIndex += functionClose;
        index = equation.indexOf("MATH");
    } 
    equation = equation.trim();
    if ((equation != null) && (!(equation.isEmpty()))) {
        list.add(equation);
    }
    return list;
}

