public autoChirp.tweetCreation.TweetGroup getTweetsFromTSVFile(java.io.File tsvFile, java.lang.String title, java.lang.String description, int delay) throws autoChirp.tweetCreation.MalformedTSVFileException {
    autoChirp.tweetCreation.TweetGroup group = new autoChirp.tweetCreation.TweetGroup(title, description);
    try {
        java.io.BufferedReader in = new java.io.BufferedReader(new java.io.FileReader(tsvFile));
        java.lang.String line = in.readLine();
        java.lang.String content;
        java.lang.String date;
        java.lang.String time;
        java.time.LocalDateTime ldt;
        autoChirp.tweetCreation.Tweet tweet;
        int row = 1;
        while (line != null) {
            if (line.equals("")) {
                line = in.readLine();
                row++;
                continue;
            }
            java.lang.String[] split = line.split("\t");
            date = split[0].trim();
            if ((date.length()) <= 7) {
                date = date.concat("-01");
            }
            time = split[1].trim();
            if (time.equals("")) {
                ldt = parseDateString(date);
                if (ldt == null) {
                    throw new autoChirp.tweetCreation.MalformedTSVFileException(row, 1, date, (((("malformed date: " + date) + "  (row: ") + row) + " column: 1)"));
                }
            }else {
                ldt = parseDateString(((date + " ") + time));
                if (ldt == null) {
                    throw new autoChirp.tweetCreation.MalformedTSVFileException(row, 1, ((date + " ") + time), (((((("malformed date or time: " + date) + " ") + time) + "  (row: ") + row) + " column: 1-2)"));
                }
            }
            java.lang.String imageUrl = null;
            if ((split.length) > 3) {
                imageUrl = split[3];
            }
            float latitude = 0;
            if ((split.length) > 4) {
                try {
                    latitude = java.lang.Float.parseFloat(split[4]);
                } catch (java.lang.NumberFormatException e) {
                    throw new autoChirp.tweetCreation.MalformedTSVFileException(row, 5, split[4], (((("malformed latitude: " + (split[4])) + "   (row: ") + row) + " column: 5)"));
                }
            }
            float longitude = 0;
            if ((split.length) > 5) {
                try {
                    longitude = java.lang.Float.parseFloat(split[5]);
                } catch (java.lang.NumberFormatException e) {
                    throw new autoChirp.tweetCreation.MalformedTSVFileException(row, 6, split[5], (((("malformed longitude: " + (split[5])) + "   (row: ") + row) + " column: 6)"));
                }
            }
            content = split[2];
            content = trimToTweet(content, null, imageUrl);
            ldt = ldt.plusYears(delay);
            if (delay == 0) {
                while (ldt.isBefore(java.time.LocalDateTime.now())) {
                    ldt = ldt.plusYears(1);
                } 
            }
            java.lang.String formattedDate = ldt.format(formatter);
            boolean midnight = false;
            if (time.contains(" 00:00")) {
                midnight = true;
            }
            if (!midnight) {
                formattedDate = formattedDate.replace(" 00:00", " 12:00");
            }
            if (ldt.isAfter(java.time.LocalDateTime.now())) {
                tweet = new autoChirp.tweetCreation.Tweet(formattedDate, content, imageUrl, longitude, latitude);
                group.addTweet(tweet);
            }
            line = in.readLine();
            row++;
        } 
        in.close();
    } catch (java.io.IOException e) {
        e.printStackTrace();
        return null;
    }
    return group;
}

