    if (n <= 750) {
        int res = n-1;
        for (int i = 0; i < n; i++) {
            for (int j = i+1; j < n; j++) {
                if ((a[i]-a[j])%(i-j))
                    continue;
                long long diff = (a[i]-a[j])/(i-j);
                int nb = 0;
                for (int k = 0; k < n; k++) {
                    if (a[i]+diff*(k-i) != a[k])
                        nb++;
                }
                res = min(nb, res);
            }
        }
        cout << res;
        return 0;
    }
    int res = 0;

