public void testEventsFired() throws java.lang.Exception {
    javax.net.ssl.SSLEngine engine = javax.net.ssl.SSLContext.getDefault().createSSLEngine();
    final java.util.concurrent.BlockingQueue<io.netty.handler.ssl.SslCompletionEvent> events = new java.util.concurrent.LinkedBlockingQueue<io.netty.handler.ssl.SslCompletionEvent>();
    io.netty.channel.embedded.EmbeddedChannel channel = new io.netty.channel.embedded.EmbeddedChannel(new io.netty.handler.ssl.SslHandler(engine), new io.netty.channel.ChannelInboundHandlerAdapter() {
        @java.lang.Override
        public void userEventTriggered(io.netty.channel.ChannelHandlerContext ctx, java.lang.Object evt) throws java.lang.Exception {
            if (evt instanceof io.netty.handler.ssl.SslCompletionEvent) {
                events.add(((io.netty.handler.ssl.SslCompletionEvent) (evt)));
            }
        }
    });
    assertTrue(events.isEmpty());
    assertTrue(channel.finishAndReleaseAll());
    io.netty.handler.ssl.SslCompletionEvent evt = events.take();
    assertTrue((evt instanceof io.netty.handler.ssl.SslHandshakeCompletionEvent));
    assertTrue(((evt.cause()) instanceof java.nio.channels.ClosedChannelException));
    evt = events.take();
    assertTrue((evt instanceof io.netty.handler.ssl.SslCloseCompletionEvent));
    assertTrue(((evt.cause()) instanceof java.nio.channels.ClosedChannelException));
    assertTrue(events.isEmpty());
}

