int main(){
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int t;
    cin>>t;

    while(t--){
        int n,m;
        cin>>n>>m;

        vector<vector<ll>> matrix(n,vector<ll>(m));

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++)
                cin>>matrix[i][j];
        }

        ll ans=1e18;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                ll temp = matrix[i][j]-(i+j);
                vector<vector<ll>> dp(n,vector<ll>(m,-1));
                dp[n-1][m-1]=(temp+n-1+m-1)<=matrix[n-1][m-1]?(matrix[n-1][m-1]-(temp+n-1+m-1)):1e16;
                for(int k=m-2;k>=0;k--){
                    dp[n-1][k]=dp[n-1][k+1];
                    if((temp+n-1+k)<=matrix[n-1][k])
                        dp[n-1][k]+=matrix[n-1][k]-(temp+n-1+k);
                    else
                        dp[n-1][k]+=1e16;
                }

                for(int k=n-2;k>=0;k--){
                    dp[k][m-1]=dp[k+1][m-1];
                    if((temp+m-1+k)<=matrix[k][m-1])
                        dp[k][m-1]+=matrix[k][m-1]-(temp+m-1+k);
                    else
                        dp[k][m-1]+=1e16;
                }

                for(int k=n-2;k>=0;k--){
                    for(int l=m-2;l>=0;l--){
                        dp[k][l]=min(dp[k+1][l],dp[k][l+1]);
                        if((temp+l+k)<=matrix[k][l])
                            dp[k][l]+=matrix[k][l]-(temp+l+k);
                        else
                            dp[k][l]+=1e16;
                    }
                }

                ans=min(ans,dp[0][0]);
            }
        }
        cout<<ans<<'\n';
    }


}

