bool isInInt(int a,int b , int x){
	if(x>= a && x < b)
		return true;
	return false;
}

int maxInt(int a , int b){
	int sum= 0 ; 
	int rem = K;
	int mak = b-a;
	for(int i = N-1 ;  mak>0   ; i--){
		if( !isInInt(a , b , sorted[i].second) ){
			if(rem > 0 )
				rem-- , sum+=sorted[i].first , mak--;
		}
		else
			sum += sorted[i].first , mak --;
	}

	return sum;
}

