#include<cstdio>
#include<iostream>
#include<vector>
#include<algorithm>
#include<string>

using namespace std;

char s[100020], a[200], b[200];

int main()
{
//	freopen("a.in", "r", stdin);
//	freopen("a.out", "w", stdout);

	scanf("%s\n", &s);                

	int n = strlen(s);           

	scanf("%s\n", &a);

	int m = strlen(a);

	scanf("%s\n", &b);

	int k = strlen(b);

	int p = 0, pp = 0;
	bool l = 0, ll = 0, r = 0, rr = 0;

	while (p < m && pp < n)
	{
		if (a[p] == s[pp])
		{
			p++; 
		}	
		pp++;
	}

	if (p == m) l = 1; 

	int p2 = 0;

	while (p2 < k && pp < n)
	{
		if (b[p2] == s[pp]) p2++;
		pp++;
	}

	if (p2 == k) ll = 1;

	p = m - 1, p2 = k - 1, pp = n - 1;

	while (p && pp)
	{
		if (a[p] == s[pp]) p--;
		pp--;
	}        

	if (!p) r = 1;

	while (p2 && pp)
	{
		if (b[p2] == s[pp]) p2--;
		pp--;
	}

	if (!p2) rr = 1;

	if (l && ll && r && rr) printf("both"); else
	if (l && ll) printf("forward"); else
	if (r && rr) printf("backward"); else
	printf("fantasy");

	return 0;
}

