#pragma GCC optimize ("O3")
#include <bits/stdc++.h>
typedef long long ll;
using namespace std;
const int N = 2e5 + 5;
const int mod = 1e9 + 7;
int n, k, ans;
int fact[N] , inv[N];
int sz[N], ways[N], waysChildren[N], dp[N];
vector<int> g[N];
int fp(int x , int y) {
    if(!y)
        return 1;
    int res = fp(x , y / 2);
    res = (1ll * res * res) % mod;
    if(y & 1)
        res = (1ll * res * x) % mod;
    return res;
}
void pre() {
    fact[0] = inv[0] = 1;
    for(int i = 1;i < N;i++) {
        fact[i] = (1ll * fact[i - 1] * i) % mod;
        inv[i] = fp(fact[i] , mod - 2);
    }
}
int ncr(int n , int r) {
    if(r > n)
        return 0;
    return (1ll * (1ll * fact[n] * inv[r]) % mod * inv[n - r]) % mod;
}
void dfs(int node, int par) {
    sz[node] = 1;
    for(auto i : g[node]) {
        if(i == par)
            continue;
        dfs(i, node);
        dp[node] = (dp[node] + dp[i]) % mod;
        sz[node] += sz[i];
        waysChildren[node] = (waysChildren[node] + ncr(sz[i], k)) % mod;
    }
    if(sz[node] >= k) {
        ways[node] = (ncr(sz[node], k) - waysChildren[node] + mod) % mod;
        dp[node] = (dp[node] + 1ll * ways[node] * sz[node] % mod) % mod;
    }
}
void solve(int node, int par) {
    ans = (ans + dp[node]) % mod;
    for(auto i : g[node]) {
        if(i == par)
            continue;
        int dp1 = dp[node], dp2 = dp[i];
        int ways1 = ways[node], ways2 = ways[i];
        int waysC1 = waysChildren[node], waysC2 = waysChildren[i];
        int sz1 = sz[node], sz2 = sz[i];
        dp[node] = (dp[node] - 1ll * sz[node] * ways[node] % mod + mod) % mod;
        dp[node] = (dp[node] - dp[i] + mod) % mod;
        dp[i] = (dp[i] - 1ll * sz[i] * ways[i] % mod + mod) % mod;
        waysChildren[node] = (waysChildren[node] - ncr(sz[i], k) + mod) % mod;
        sz[node] -= sz[i];
        ways[node] = (ncr(sz[node], k) - waysChildren[node] + mod) % mod;
        dp[node] = (dp[node] + 1ll * sz[node] * ways[node] % mod) % mod;
        sz[i] += sz[node];
        waysChildren[i] = (waysChildren[i] + ncr(sz[node], k)) % mod;
        ways[i] = (ncr(sz[i], k) - waysChildren[i] + mod) % mod;
        dp[i] = (dp[i] + 1ll * sz[i] * ways[i] % mod);
        dp[i] = (dp[i] + dp[node]) % mod;
        solve(i, node);
        dp[node] = dp1, dp[i] = dp2;
        ways[node] = ways1, ways[i] = ways2;
        waysChildren[node] = waysC1, waysChildren[i] = waysC2;
        sz[node] = sz1, sz[i] = sz2;
    }
}
int main() {
    ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0);
    cin >> n >> k;
    pre();
    for(int i = 1;i < n;i++) {
        int u, v;
        cin >> u >> v;
        g[u].push_back(v);
        g[v].push_back(u);
    }
    dfs(1, 1);
    solve(1, 1);
    cout << ans;
    return 0;
}

