long go(int l, int r, int k){
	unsigned int key = 2 * l*n*k + r * k * 2 + k * 2 + (l <= r);
	if (dp.count(key)) return dp[key];
	if (l <= r){
		long mx = -oo;
		if (l + k + r < n)
			mx = max(mx, go(l + k, r, k) - L[l] + L[l + k]);
		if (l + k + 1 + r < n)
			mx = max(mx, go(l + k + 1, r, k + 1) - L[l] + L[l + k + 1]);

