#include <bits/stdc++.h>
using namespace std;
const int MX = 2e5 + 100;
int v[MX], BIT[MX];
inline void add(int p, int v) {
	for(int i = p; i < MX; i += i & -i) BIT[i] += v;
}
inline int query(int p) {
	int re = 0;
	for(int i = p; i; i -= i & -i) re += BIT[i];
	return re;
}
main () {
	#define endl '\n'
	ios_base::sync_with_stdio(false);
	cin.tie(0);
	int n, q; cin >> n >> q;
	for(int i = 1; i <= n; i ++) {
		cin >> v[i];
		add(i, v[i]);
	}
	while(q--) {
		int p, d; cin >> p >> d;
		add(p, d - v[p]); v[p] = d;
		int sum = 0, ptr = 1, ans = -1;
		while(ptr <= n) {
			int l = ptr, r = n;
			while(l != r) {
				int m = l + r >> 1;
				if(query(m) < sum) l = m + 1;
				else r = m;
			}
			if(query(l) == 2 * v[l]) {
				ans = l; break;
			}
			sum = max(sum + 1, 2 * query(l));
			ptr = l + 1;
		}
		cout << ans << endl;
	}
}

