bool check (int x) {
	while (x) {
		if (x % 10 != 4 && x % 10 != 7) return 0;
		x /= 10;       
	}
	return 1;
}

int bp (int x, int n) {
	int res = 1;
	while (n) {
		if (n & 1) res = (res * x) % mod;
		x = (x * x) % mod;
		n >>= 1;
	}
	return res;
}

int C (int n, int k) {
	if (n < k || k < 0 || n < 0) return 0ll;
	return (((f[n] * inv[k]) % mod) * inv[n - k]) % mod;
}

