void solve() {
  int n, m;
  cin >> n >> m;
  vi v(n);
  vi index(n + 1);
  for (int i = 0; i < n; i++) {
    cin >> v[i];
    index[v[i]] = i;
  }
  vector<pi> p;
  set<pi> s;
  for (int i = 0; i < m; i++) {
    int a, b;
    cin >> a >> b;
    if (index[a] > index[b]) {
      swap(a, b);
    }
    s.insert({index[a], index[b]});
    // p.pb({index[a], index[b]});
  }
  for (auto i : s) {
    p.pb(i);
  }
  sort(all(p), [](pi a, pi b) { return a.ss < b.ss; });
  int i = 0;
  int j = 0;
  int k = 0;
  //debug(p);
  int ans = n;
  while (j < n) {
    if (k < p.size() && j == p[k].ss) {
      debug(i, j - 1);
      ans += calc_pair(i, j - 1);
      i = max(i, p[k].ff + 1);
      k++;
    }
    j++;
  }
  debug(i);
  ans += calc_pair(i, n - 1);
  cout << ans << '\n';
}

