import java.awt.Point;
import java.util.ArrayDeque;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Deque;
import java.util.HashMap;
import java.util.HashSet;
import java.util.List;
import java.util.Scanner;
import java.util.Set;

public class C {

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		List<Project> graph = new ArrayList<>();
		int begin = 0;
		for (int i = 0; i < n; i++) {
			String name = in.next();
			if (name.equals(" "))
				name = in.next();
			int ver = in.nextInt();
			int k = in.nextInt();
			Project now = new Project(name, ver);
			for (int j = 0; j < k; j++) {
				String nameB = in.next();
				int verB = in.nextInt();
				Project p1 = new Project(nameB, verB);
				int ind = myindexOf(p1, graph);
				if (ind < 0) {
					graph.add(p1);
					ind = graph.size() - 1;
				}
				now.add(ind);
			}
			int ind = myindexOf(now, graph);
			if (ind < 0) {
				graph.add(now);
				ind = graph.size() - 1;
			} else {
				graph.remove(ind);
				graph.add(ind, now);
			}
			if (i == 0)
				begin = ind;

		}
		Project beginer = graph.get(begin);
		Project[] ans = bfs(begin, graph, beginer);

		Arrays.sort(ans);
		System.out.println(ans.length - 1);
		for (int i = 0; i < ans.length; i++)
			if (!ans[i].equals(beginer))
				System.out.println(ans[i].name + " " + ans[i].ver);

	}

	static Project[] bfs(int v, List<Project> graph, Project beginer) {
		boolean used[] = new boolean[graph.size()];
		HashMap<String, Point> hm = new HashMap<>();
		hm.put(beginer.name, new Point(beginer.ver, v));
		Set<Integer> setFU = new HashSet<>();
		int[] dist = new int[graph.size()];

		used[v] = true;
		Deque<Integer> q = new ArrayDeque<>();
		q.push(v);
		while (q.size() > 0) {
			int from = q.pop();
			if (!setFU.contains(from))
				for (int to : graph.get(from).list) {
					if (used[to])
						continue;
					used[to] = true;

					dist[to] = dist[from] + 1;
					Project p = graph.get(to);
					if (!hm.containsKey(p.name)) {
						hm.put(p.name, new Point(p.ver, to));
						q.push(to);
					} else {
						Point point = hm.get(p.name);
						if (dist[to] < dist[point.y] | (dist[to] == dist[point.y] && p.ver > point.x)) {
							setFU.add(point.y);
							hm.put(p.name, new Point(p.ver, to));
							q.push(to);
						}
					}

				}
		}
		Project[] ans = new Project[hm.size()];
		int i = 0;
		for (String name : hm.keySet()) {
			int ver = hm.get(name).x;
			ans[i] = new Project(name, ver);
			i++;
		}
		return ans;

	}

	static int myindexOf(Project p, List<Project> graph) {
		for (int z = 0; z < graph.size(); z++)
			if (graph.get(z).equals(p))
				return z;
		return -1;
	}

	static class Project extends Object implements Comparable<Project> {
		String name;
		int ver;
		List<Integer> list;

		Project(String name, int ver) {
			this.name = name;
			this.ver = ver;
			list = new ArrayList<>();
		}

		void add(int v) {
			list.add(v);
		}

		boolean equals(Project p) {
			return p.name.equals(name) & p.ver == ver;

		}

		@Override
		public String toString() {
			return name + " " + ver;
		}

		@Override
		public int compareTo(Project o) {
			return name.compareTo(o.name);
		}

	}

}

