const int N = 1<<22;
int g, h;
int a[N];
int idx[N];

bool can(int i) {
  if (a[i] == 0) return false;
  const int dep = 1 << g;
  if (i >= dep) return true;
  int l = i*2;
  int r = i*2+1;
  if (a[l] > a[r]) return can(l);
  return can(r);
}

void remove(int i) {
  int l = i*2;
  int r = i*2+1;
  if (a[l] == 0 && a[r] == 0) {
    a[i] = 0;
  } else {
    if (a[l] > a[r]) {
      a[i] = a[l];
      idx[a[l]] = i;
      remove(l);
    } else {
      a[i] = a[r];
      idx[a[r]] = i;
      remove(r);
    }
  }
}

void prt() {
