    nums.push_back(a[i]);
    sum += a[i];
  }
  sort(nums.begin(), nums.end(), greater<int>());
  int left = (1 << h) - (1 << g);
  for (int x : nums) {
    if (left == 0) break;
    int i = idx[x];
    assert(i > 0);
    if (can(i)) {
      remove(i);
      sum -= x;
      left--;
      rem.push_back(i);
    }
  }
  assert(left == 0);
  //check();
