int n,p[100001],h[100001];
int main(){
	scanf("%d",&n);
	for(int i=0;i<n;i++){
		scanf("%d",&p[i]);
	}
	int prs=2,pk=0,state=0,hst=-1,res=0,psk=0,flg=0;
	if(p[1]>p[0])
		state=1;
	else
		state=0;
	for(int i=2;i<n;i++){
		if(p[i]>p[i-1]&&state==1){
			if(i==n-1){
				prs++;
				if(hst<=prs)
					res=0;
			}
			prs++;
		}
		else if(p[i]>p[i-1]&&state==0)
		{
			if((psk%2==1&&prs==psk)||(psk%2==1&&prs==psk-1)||(prs%2==1&&prs-1==psk)){
				if(flg==max(prs,psk)||hst==max(prs,psk)){
					res=0;
				}
				else if(max(prs,psk)>flg&&max(prs,psk)>hst){
					res=1;
					flg=max(psk,prs);
				}
				
			}
			hst=max(prs,hst);
			hst=max(hst,psk);
			state=1;
			prs=2;
		}
		else if(p[i]<p[i-1]&&state==1){
			psk=prs;
			if(psk>hst)res=0;
			state=0;
			prs=2;
		}
		else{
			if(i==n-1){
				prs++;
				if(prs>=hst)res=0;
				if((psk%2==1&&prs==psk)||(psk%2==1&&prs==psk-1)||(prs%2==1&&prs-1==psk)){
				if(flg==max(prs,psk)||hst==max(prs,psk)){
					res=0;
				}
				else if(max(prs,psk)>flg&&max(prs,psk)>hst){
					res=1;
					flg=max(psk,prs);
				}
				}
			}
			prs++;
		}
	}
	printf("%d",res);
}

