import java.io.BufferedOutputStream;
import java.io.BufferedReader;
import java.io.File;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.FileReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.io.PrintStream;
import java.util.StringTokenizer;
import java.util.stream.IntStream;

import static sun.swing.MenuItemLayoutHelper.max;


public class Demalys {

    public static void operate() throws Exception {
        int n = in.nextInt();
        int a[][] = new int[n + 1][4];
        IntStream.rangeClosed(1, n).forEach(i -> {
            int o = in.nextInt();
            if (o == 0) {
                a[i][0] = max(a[i - 1][0], a[i - 1][1], a[i - 1][2]);
            }
            if (o == 1 || o == 3) {
                a[i][1] = max(a[i - 1][0] + 1, a[i - 1][2] + 1);
            }

            if (o == 2 || o == 3) {
                a[i][2] = max(a[i - 1][0] + 1, a[i - 1][1] + 1);
            }

        });

        out.println(n-max(a[n][0],a[n][1],a[n][2]));
    }

    static Exception exception;
    private static Scanner in;
    private static Output out;
    static boolean isFile = false;

    public static void main(String[] args) throws Exception {
        Thread thread = new Thread(null, new Runnable() {
            @Override
            public void run() {
                try {
                    initReaderWriter();
                    operate();
                    out.close();
                } catch (Exception ex) {
                    exception = ex;
                }
            }
        }, "", 1 << 26);
        thread.start();
        thread.join();

        if (exception != null) {
            throw exception;
        }
    }


    private static void initReaderWriter() throws Exception {
        if (isFile) {
            in = new Scanner("input.txt");
            out = new Output(new File("output.txt"));
        } else {
            in = new Scanner();
            out = new Output(System.out);
        }
    }

    private static boolean log = false;

    public static void log(String msg) {
        if (log) {
            out.println(msg);
            out.flush();
        }
    }

    private static class Scanner {

        StringTokenizer st = null;
        BufferedReader bf;

        public Scanner() {
            bf = new BufferedReader(new InputStreamReader(System.in));
        }

        public Scanner(String fileName) throws FileNotFoundException {
            bf = new BufferedReader(new FileReader(fileName));
        }

        public String next() {
            while (st == null || !st.hasMoreTokens())
                st = new StringTokenizer(readLine());
            return st.nextToken();
        }

        public String nextLine() {
            return readLine();
        }

        public String readLine() {
            try {
                return bf.readLine();
            } catch (IOException ex) {
                ex.printStackTrace();
                throw new RuntimeException(ex.getMessage(), ex.getCause());
            }
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

        public long nextLong() {
            return Long.parseLong(next());
        }

        public double nextDouble() {
            return Double.parseDouble(next());
        }
    }


    private static class Output extends PrintStream {

        public Output(OutputStream out) {
            super(new BufferedOutputStream(out));
        }

        public Output(File file) throws FileNotFoundException {
            super(new BufferedOutputStream(new FileOutputStream(file)));
        }
    }

    private static void printMemory() {
        Runtime runtime = Runtime.getRuntime();

        long maxMemory = runtime.maxMemory();
        long allocatedMemory = runtime.totalMemory();
        long freeMemory = runtime.freeMemory();

        long div = 1L * 1024 * 1024;
        // long div = 1L ;

        System.out.println("used memory[mgb]: " + (runtime.totalMemory() - runtime.freeMemory()) / div);

        System.out.println();
    }

    static long appCurrentTime = System.currentTimeMillis();
    static long appCurrentTimeNano = System.nanoTime();

    private static void printCurrentTime() {
        out.flush();
        System.out.println("Time: " + (System.currentTimeMillis() - appCurrentTime));
        System.out.println("Time Nano: " + (System.nanoTime() - appCurrentTimeNano));
    }
}

