#include <iostream>
#include <functional>
#include <vector>
#include <limits>
#include <algorithm>
using namespace std;

typedef pair<int, int> pii;

int top(int x, vector<int>& f) {
    if(f[x] != x) f[x] = top(f[x], f);
    return f[x];
}
void merge(int x, int y, vector<int>& f) {
    x = top(x, f);
    y = top(y, f);
    f[x] = y;
}

vector<vector<int> > lca;
vector<int> vis, ancestor, f;
vector<pii> ab;
vector<vector<pii>> road;

void _lca(int u) {
    vis[u] = 1;
    ancestor[top(u, f)] = u;
    for(int i = 0; i < road[u].size(); ++i) {
        pii& v = road[u][i];
        if(!vis[v.first]) {
            _lca(v.first);
            merge(u, v.first, f);
            ancestor[top(u, f)] = u;
        }
    }

    for(int i = 0; i < ab.size(); ++i) {

        if(lca[ab[i].first][ab[i].second] != -1) continue;

        if(ab[i].first == u && vis[ab[i].second]|| ab[i].second == u && vis[ab[i].first]) {
            u = ab[i].first == u? ab[i].second : ab[i].first;
            //cout << ab[i].first << " " << ab[i].second << " " << ancestor[top(u, f)] << endl;
            lca[ab[i].first][ab[i].second] = ancestor[top(u, f)];
            lca[ab[i].second][ab[i].first] = ancestor[top(u, f)];
        }
    }
}

void cal_lca(vector<pii>& ab, int n) {
    f = vector<int>(n);
    for(int i = 0; i < n; ++i) f[i] = i;
     ancestor = vector<int>(n);
    _lca(0);
}
int dfs(int u, int e, vector<int>& c, vector<int>& lca_c, vector<int>& ans) {
    //u的祖先为p，e为p到u的边
    //返回经过e的次数，以u为根结点的树的任意一个结点设为i，则有
    //dfs(u) = sum(c[i]) - sum(lca_c[i])

    vis[u] = 1;
    int sum = 0;
    for(int i = 0; i < road[u].size(); ++i) {
        pii& v = road[u][i];
        if(!vis[v.first])
            sum += dfs(v.first, v.second, c, lca_c, ans);
    }

    sum -= lca_c[u];
    sum += c[u];

    return ans[e] = sum;
}

int main() {

    int n, k;

    cin >> n;

    road = vector<vector<pii>>(n);

    for(int a, b, i = 1; i < n; ++i) {
        cin >> a >> b;
        road[a - 1].push_back(make_pair(b - 1, i));
        road[b - 1].push_back(make_pair(a - 1, i));
    }

    cin >> k;

    vector<int> c(n, 0), lca_c(n, 0);

    ab = vector<pii>(k);

    for(int i = 0;i < k; ++i) {
        cin >> ab[i].first >> ab[i].second;
        --ab[i].first, --ab[i].second;
    }

    lca = vector<vector<int> >(n, vector<int>(n, -1));

    vis = vector<int>(n);
    cal_lca(ab, n);

    for(int i = 0;i < k; ++i) {
        int a = ab[i].first;
        int b = ab[i].second;
        ++c[a], ++c[b];
        lca_c[lca[a][b]] += 2;
        //cout << a << " " << b << " " << lca[a][b] << endl;
    }
    fill(vis.begin(), vis.end(), 0);
    vector<int> ans(n, 0);

    dfs(0, 0, c, lca_c, ans);
    cout << ans[1];
    for(int i = 2; i < n; ++i) cout << " " << ans[i];cout << endl;
    return 0;
}

