void dfs(int v)
{
	dd[v] = maxn;
	mark[v] = true;
	st.pb(v);
	For(i,0,maxlg)
		p[v][i] = -1;
	for(int i = 0 ; n(st)-(1<<i) >= 0 ; ++i )
		p[v][i] = st[n(st)-1-(1<<i)];
	For(i,0,n(adj[v]))
	{
		if(!mark[adj[v][i]])
		{
			dep[adj[v][i]] = dep[v] +1;
			dfs(adj[v][i]);
			dd[v] = min(dd[v],dd[adj[v][i]]);
			if(dd[adj[v][i]] < dep[v])
				isb[v] = true;

		}
		else
		{
			dd[v] = min(dd[v],dep[adj[v][i]]);
			if(dep[adj[v][i]] < dep[v]-1)
				isb[v] = true;
		}
	}
	st.pop_back();
}

int getp(int x , int y , int k)
{
//	cerr << x+1 << " " << y+1 << endl;
	if(x==y)
		return x;
	if(dep[x] > dep[y])
		return getp(y,x,k);
	if(dep[x] < dep[y])
	{
//		cerr << dep[y]-dep[x]  <<  " " << llp[dep[y]-dep[x]] << " " << p[y][llp[dep[y]-dep[x]]] << endl;
		return getp(x,p[y][llp[dep[y]-dep[x]]],k);
	}
	while(k)
	{
		if(p[x][k]!=p[y][k])
			return getp(p[x][k],p[y][k],k-1);
		k--;
	}
//	cerr << "RETURNED " << p[x][1] << endl;
	return p[x][1];
}
